    function initMap(){
        var dataLat = $('#mapGardens').data('lat'),
            dataLng = $('#mapGardens').data('lng'),
            $garden = { lat: dataLat, lng: dataLng };
        var mapOptions = {
            zoom: 16,
            minZoom: 6,
            maxZoom: 17,
            zoomControl:true,
            zoomControlOptions: {
      			style:google.maps.ZoomControlStyle.DEFAULT
    	    },
            center: $garden,
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            scrollwheel: false,
            panControl:false,
            mapTypeControl:false,
            scaleControl:false,
            streetViewControl:false,
            overviewMapControl:false,
            rotateControl:false
        }

        var map = new google.maps.Map(document.getElementById('mapGardens'), mapOptions);
        var image = new google.maps.MarkerImage("data:image/png;base64,"
        , null, null, null, new google.maps.Size(63,92));
        var marker = new google.maps.Marker({
            position: $garden,
            icon:image,
            map: map,
            title: 'Robinsons The Gardens'
        });

        var infowindow = new google.maps.InfoWindow({
      		content: $('#mapGardensInfo').html()
      	});


        google.maps.event.addListener(marker, 'click', function() {
      		infowindow.open(map,marker);
      	});


        google.maps.event.addDomListener(window, 'resize', function() {
            map.setCenter($garden);
            panIfNeeded();
        });

        panIfNeeded();

        function panIfNeeded() {
            var panDistance = Math.max(1280*-0.15, $(window).width()*-0.15);

            if($(window).width() > 1024) {
                map.panBy(panDistance, 0);
            }
        }
    }

